// 簡單調試版本
const items = $input.all();
const firstItem = items[0];
const jsonData = firstItem.json;

// 獲取 body
const body = jsonData.body || jsonData || {};

console.log('=== DEBUG INFO ===');
console.log('Full JSON:', JSON.stringify(jsonData, null, 2));
console.log('Body:', JSON.stringify(body, null, 2));

// 創建回應
const response = {
  success: true,
  debug: {
    received_body: body,
    endpoint: body.endpoint,
    has_body: !!jsonData.body
  },
  message: 'Debug mode - 檢查請求內容'
};

// 測試 OpenSubtitles API
if (body.endpoint === '/movies/popular') {
  // 直接返回測試數據
  response.data = [
    { id: 1, title: 'Inception', year: 2010, imdb_id: 'tt1375666' },
    { id: 2, title: 'The Matrix', year: 1999, imdb_id: 'tt0133093' }
  ];
  response.message = '熱門影片（測試數據）';
}

return { json: response };
