// 嘗試獲取請求數據
const inputData = $input.first();
const jsonData = inputData.json;

// 記錄調試信息
console.log('Full input:', JSON.stringify(inputData, null, 2));

// 獲取 body
let body = jsonData.body || jsonData || {};

// 根據 endpoint 處理
const endpoint = body.endpoint || '';
const result = {
  success: true,
  endpoint: endpoint,
  received_data: body,
  debug_info: {
    has_body: !!jsonData.body,
    body_keys: Object.keys(body || {}),
    json_data_keys: Object.keys(jsonData || {})
  },
  message: `接收到請求: ${endpoint}`
};

// 如果是熱門影片請求
if (endpoint === '/movies/popular') {
  result.data = [
    { id: 1, title: '測試電影 1', year: 2023, imdb_id: 'tt1234567' },
    { id: 2, title: '測試電影 2', year: 2022, imdb_id: 'tt7654321' }
  ];
  result.message = '熱門影片抓取成功（測試數據）';
}
// 如果是搜尋請求
else if (endpoint === '/movies/search') {
  result.query = body.query || '';
  result.data = [
    { id: 3, title: `搜尋結果: ${body.query}`, year: 2021, imdb_id: 'tt1111111' }
  ];
  result.message = '影片搜尋成功（測試數據）';
}

return [result];
